package 확인문제;

public class 문제풀이2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 30;
		int num2 = 40;

		System.out.println("두수를 더한 값은 " + (num1 + num2));
		System.out.println("num1 나누기 num2는 " + (num1 / num2));
		System.out.println("num1 나누기 num2에서 나머지는 " + (num1 % num2));

	}

}
